mapper.select:
select
from ku_old_car where 1=1
order by addtime desc limit #{firstResult,jdbcType=INTEGER},#{maxResult,jdbcType=INTEGER}

		select itemid, userid, carid,distance,  areaid as cityid, 
    		carload, carlong,carprice,carimg1, addtime, state,discharge
			,(select title from ku_site_category where itemid=brandname) as brandName
			,(select title from ku_site_category where itemid=cartype) as carTypeName
			,(select areaname from ku_city where areaid=cityid) as areaName 
		from ku_old_car 
		where areaid in ( 
						select a.areaid 
						from ku_city a 
						join ku_city b 
						on a.parentid=b.areaid 
						where a.parentid=#{cityId,jdbcType=INTEGER} or a.areaid=#{cityId,jdbcType=INTEGER} or b.parentid=#{cityId,jdbcType=INTEGER}) 
		and state=1

	      	and cartype = #{typeId,jdbcType=INTEGER}



	      	and discharge = #{blowDownId,jdbcType=INTEGER}



	      	and brandname = #{brandId,jdbcType=INTEGER}



	      	and amount = #{axletreeId,jdbcType=INTEGER}



	      	and carprice >= #{priceStart,jdbcType=DECIMAL} and carprice < #{priceEnd,jdbcType=DECIMAL}


	      	and carprice >= #{priceStart,jdbcType=DECIMAL}


	      	and carprice  < #{priceEnd,jdbcType=DECIMAL}



	      	and carlong >= #{lenStart,jdbcType=DECIMAL} and carlong < #{lenEnd,jdbcType=DECIMAL}


	      	and carlong >= #{lenStart,jdbcType=DECIMAL}


	      	and carlong  < #{lenEnd,jdbcType=DECIMAL}



	      	and distance >= #{mileageStart,jdbcType=DECIMAL} and distance < #{mileageEnd,jdbcType=DECIMAL}


	      	and distance >= #{mileageStart,jdbcType=DECIMAL}


	      	and distance  < #{mileageEnd,jdbcType=DECIMAL}



	      	and carload >= #{tonnageStart,jdbcType=DECIMAL} and carload < #{tonnageEnd,jdbcType=DECIMAL}


	      	and carload >= #{tonnageStart,jdbcType=DECIMAL}


	      	and carload < #{tonnageEnd,jdbcType=DECIMAL}



	    	and guapai between date_sub(now(),interval #{ageStart,jdbcType=DECIMAL} year ) and date_sub(now(),interval #{ageEnd,jdbcType=DECIMAL} year )


	      	and guapai > date_sub(now(),interval #{ageStart,jdbcType=DECIMAL} year )


	      	and guapai < date_sub(now(),interval #{ageEnd,jdbcType=DECIMAL} year )

		order by addtime desc;
