mapper.select:
select
from ku_member where 1=1
order by addtime desc limit #{firstResult,jdbcType=INTEGER},#{maxResult,jdbcType=INTEGER}

select count(*)
from ku_member where 1=1 

	and mobile =#{member_mobile}

mapper.update:
		update ku_member
   		 set password=#{password,jdbcType=VARCHAR},
   		 edittime = #{edittime,jdbcType=INTEGER}
   		 where mobile= #{mobile,jdbcType=VARCHAR}
